import React from "react";
import styled from "@emotion/styled";
import theme from "../../styles/theme";

const Header = styled.header`
  margin-bottom: ${theme.margin.laptop};

  & h1 {
    font-weight: 600;
    margin-bottom: 6px;
  }

  & p:first-of-type {
    color: #086bce;
    font-weight: 500;
    margin-bottom: 6px;
  }

  & p:last-of-type {
    color: #a1a1a1;
    font-weight: 500;
  }
  @media screen and (max-width: 768px) {
    margin-bottom: ${theme.margin.tablet};
  }
`;

const Line = styled.hr`
  margin-bottom: 40px;
  @media screen and (max-width: 768px) {
    margin-bottom: 2rem;
  }
`;

const Contents = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;

  & div {
    margin-bottom: 2.5rem;

    @media screen and (max-width: 768px) {
      margin-bottom: 2rem;
    }
  }
  @media screen and (max-width: 768px) {
    display: flex;
    flex-direction: column;
    margin-bottom: 2rem;
  }

  & div:last-child {
    margin-bottom: 0;
  }

  & h3 {
    margin-bottom: 1rem;

    @media screen and (max-width: 768px) {
      margin-bottom: 0.5rem;
    }
  }
`;

const ProjectDetail = (props) => {
  const goals = props.goal.split(",");
  const roles = props.role.split(",");
  const squaredData = (DATA) =>
    DATA.map((element) => <p key={Math.random(100)}>{element}</p>);
  return (
    <>
      <meta itemProp="headline" content={props.title} />
      <Header>
        <p>{props.company}</p>
        <h1>{props.title}</h1>
        <p>{props.year}</p>
      </Header>
      <Line />
      <Contents>
        <div>
          <h3>Project goals</h3>
          {squaredData(goals)}
        </div>
        <div>
          <h3>Roles</h3>
          {squaredData(roles)}
        </div>
        <div style={{ gridColumn: "1/3" }}>
          <h3>Summary</h3>
          <p>{props.summary}</p>
        </div>
      </Contents>
    </>
  );
};

export default ProjectDetail;
