import React, { useState } from "react";
import LargeBox from "../components/LargeBox";
import ProjectCard from "../components/ProjectCard";
// import UpMargin from "../components/UpMargin";
import styles from "./Work.module.css";
import projectList from "../ProjectList";
import ProjectTable from "../components/ProjectTable";

const Work = () => {
  const [card, setCard] = useState(true);
  const [list, setList] = useState(false);

  const hideCard = () => {
    setCard(false);
    setList(true);
  };
  const showCard = () => {
    setCard(true);
    setList(false);
  };
  console.log(projectList[0].title);
  return (
    <section className="container">
      <LargeBox
        title="LET'S MAKE HISTORY"
        details="I'm Beomsoo Son, an engineer/designer based in Suwon, South Korea
        focused on Industrial Design & Engineering and Product Design."
      />
      <div className={styles.stateBtn_wrapper}>
        <button className={styles.stateBtn} onClick={showCard}>
          Card View
        </button>
        <button className={styles.stateBtn} onClick={hideCard}>
          List View
        </button>
      </div>
      {list === true ? <ProjectTable /> : null}
      {card === true ? (
        <div className={styles.card_area}>
          {projectList.map((post) => {
            return (
              <ProjectCard
                title={projectList[0].title}
                links={projectList.links}
                company={projectList.company}
                year={projectList.year}
                background={projectList.background}
              />
            );
          })}
        </div>
      ) : null}
    </section>
  );
};

export default Work;

