import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Hamburger.css";
import styled from "@emotion/styled";
import Hamburger from "./Hamburger";

const Wrapper = styled.nav`
  width: 75rem; // 1200px
  display: grid;
  grid-template-columns: 1fr 1fr;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  transition: all 0.5s ease-in-out;

  @media screen and (max-width: 1400px) {
    width: 90vw;
  }

  @media screen and (max-width: 768px) {
    display: grid;
    align-items: center;
    position: relative;
    width: 90vw;
  }
`;

const SelectArea = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  transition: all 0.5s ease-in-out;

  & p,
  a {
    font-size: 1rem; //16px
    margin-left: 2.5rem; //40px
    font-weight: 400;
  }

  @media screen and (max-width: 768px) {
    display: none;
  }
`;

const Navigation = () => {
  const [hamburger, setHamburger] = useState(false);
  const menuStatus = () => {
    setHamburger(!hamburger);
  };

  const Container = styled.section`
    width: 100vw;
    height: ${hamburger === false ? "4rem" : "20rem"}; //64px
    display: flex;
    justify-content: center;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(15px);
    -webkit-backdrop-filter: blur(15px);
    position: -webkit-sticky;
    position: sticky;
    top: 0px;
    z-index: 2;
    transition: all 0.5s ease-in-out;

    border-bottom: solid 1px #f3f3f3;
  `;

  return (
    <Container>
      <Wrapper>
        <>
          <Link to="/Work" style={{ fontSize: "1.25rem", fontWeight: "600" }}>
            Beomsoo Son
          </Link>
          <SelectArea>
            <Link to="/Work">Work</Link>
            <Link to="/About">About</Link>
            <a href="https://www.beomsoo.me">Blog</a>
            <a href="mailto: beskar.son@gmail.com">Contact</a>
          </SelectArea>
          <Hamburger hamburger={hamburger} menuStatus={menuStatus()} />
        </>
      </Wrapper>
    </Container>
  );
};

export default Navigation;
