import React from "react";
import { css } from "@emotion/react";

const divStyle = css`
  background-color: hotpink;
  font-size: 24px;
  border-radius: 4px;
  padding: 32px;
  text-align: center;
  &:hover {
    color: white;
  }
`;

const LargeBox = (background) => {
  return (
    <div css={divStyle} style={{ background: `url(${background})` }}></div>
  );
};

export default LargeBox;
