export const size = {
  largest: "75em", // 1200px
  large: "43.75em", // 900px
  medium: "90vw", // 700px
  small: "31.25em", // 500px
  smallest: "25em", // 400px
};

export const margin = {
  laptop: "",
  tablet: "",
  medium: "",
};

const theme = {
  mainColor: "#0000ff",
  mq: {
    laptop: `@media only screen and (min-width: ${size.largest})`,
    tablet: `@media only screen and (min-width: ${size.large})`,
    mobile: `@media only screen and (min-width: ${size.small})`,
  },
  width: {
    laptop: size.largest,
    tablet: size.large,
    mobile: size.medium,
  },
};

export default theme;
