import React from "react";
import { Link } from "react-router-dom";
import styled from "@emotion/styled";

const HamburgerContainer = styled.div`
  display: none;
  flex-direction: column;
  justify-content: flex-end;
  transition: all 0.5s ease-in-out;

  @media screen and (max-width: 768px) {
    display: flex;
    align-items: flex-end;
  }
`;

const Label = styled.label`
  display: flex;
  flex-direction: column;
  width: 30px;
  cursor: pointer;

  & span {
    background: #222222;
    border-radius: 10px;
    height: 3px;
    margin: 3px 0;
    transition: 0.4s cubic-bezier(0.68, -0.6, 0.32, 1.6);
  }

  & span:nth-of-type(1) {
    width: 50%;
  }

  & span:nth-of-type(2) {
    width: 100%;
  }

  & span:nth-of-type(3) {
    width: 75%;
  }

  &[type="checkbox"] {
    display: none;
  }
`;
const Hamburger = (hamburger, menuStatus) => {
  const HamburgerMenu = styled.div`
    display: ${hamburger === false ? "none" : "flex"};
    flex-direction: column;
    transition: all 0.5s ease-in-out;

    & p,
    a {
      height: 3rem;
    }
  `;

  return (
    <>
      <HamburgerContainer>
        <Label htmlFor="check">
          <input type="checkbox" id="check" onClick={menuStatus} />
          <span className={hamburger ? "bar1_checked" : ""}></span>
          <span className={hamburger ? "bar2_checked" : ""}></span>
          <span className={hamburger ? "bar3_checked" : ""}></span>
        </Label>
      </HamburgerContainer>
      <HamburgerMenu>
        <Link to="/Work" onClick={menuStatus}>
          Work
        </Link>
        <Link to="/About" onClick={menuStatus}>
          About
        </Link>
        <a href="https://www.beomsoo.me">Blog</a>
        <a href="mailto: beskar.son@gmail.com">Contact</a>
      </HamburgerMenu>
    </>
  );
};

export default Hamburger;
